// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;

import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import {ILpToken} from "../intf/ILpToken.sol";

contract LpToken is ERC20, ILpToken, Ownable {
    constructor(string memory name, string memory symbol) ERC20(name, symbol) public {}

    function mint(address _to, uint256 _amount) external override onlyOwner {
        _mint(_to, _amount);
    }

    function burn(address _owner, uint256 _amount) external override onlyOwner {
        _burn(_owner, _amount);
    }
}
